import * as React from 'react';
import LoginButton from '../components/LoginButton';
import { StyleSheet, Button } from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';

import AsyncStorage from '@react-native-async-storage/async-storage';

// import authHandler from "../components/utils/authenticationHandler";

const getData = async () => {
  try {
    const value = await AsyncStorage.getItem('code')
    if(value !== null) {
      console.log(value)
      return value
    }
  } catch(e) {
    console.log(e)
  }
}

const removeItemValue = async (key: string) => {
  try {
      await AsyncStorage.removeItem(key);
      return true;
  }
  catch(exception) {
      return false;
  }
}


export default function Login() {

  const [loggedIn, setLoggedIn ] = React.useState(false)

  getData().then(res => {
    if(res !== null){
      setLoggedIn(true)
    } else {
      setLoggedIn(false)
    }
  })

  return (
    <View style={styles.container}>
      {/* <Text style={styles.title}>Login</Text> */}
      {loggedIn ? <Button onPress={removeItemValue("code")} title="Log Out"/> : <LoginButton/>}
      {/* <LoginButton/> */}
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" />
      <EditScreenInfo path="/screens/TabTwoScreen.tsx" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
