import axios from 'axios';
import * as React from 'react';
import { StyleSheet, Button } from 'react-native';
import { Text, View } from './Themed';
import AsyncStorage from '@react-native-async-storage/async-storage';

export default function Player(props: any) {

  const initialState = {
    token: null,
    item: {
      album: {
        images: [{ url: "" }]
      },
      name: "",
      artists: [{ name: "" }],
      duration_ms: 0
    },
    is_playing: "Paused",
    progress_ms: 0,
    no_data: false,
  };

  const [playing, setPlaying] = React.useState(initialState);
  const userToken =  AsyncStorage.getItem('token')

  function getCurrentlyPlaying(token: any) {
    const headers = {
      headers: { Authorization: "Bearer " + token}
    }

    axios.get( "https://api.spotify.com/v1/me/player", headers).then(res => {
      console.log(res);
    }).catch(err => {
      console.log(err)
    })
  }

  React.useEffect(() => {
    // const userToken =  AsyncStorage.getItem('token');
    const userToken = 'BQCxj0c2QigVbd-bZ4mnG13RXDznSypli0-sO1VZt1-L1SIuVBqDk2hdhQO-z5s-qXQzPQfhLlK1KlW35vrUsivh25QmLfHltin2B991UW-84huhvyYqzUQLtx6IhtKx1wCSjiNB5WbOu9WGjPtlfZPY_CYWbFCwTsfy6rcxDZuNJs-SqL7OGKY'
    console.log(userToken)
    getCurrentlyPlaying(userToken)
  }, [])

  return (
    <View style={styles.container}>
      <Text>{playing.item.artists[0].name}</Text>
      <Text>{playing.is_playing ? "Playing" : "Paused"}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
