import * as React from 'react';
import { Platform, StyleSheet } from 'react-native';
import { Text, View} from '../components/Themed';
import LoginButton from '../components/LoginButton';
import Album_background from '../assets/images/albumsbackground.svg';
// import authHandler from "../components/utils/authenticationHandler";
import SvgBackground from '../assets/SvgBackground';

const Login = () => {

  return (
    <View style={styles.container}>
      <View style={styles.backgroundWrapper}>
        <View style={styles.wrapper}>
          <Text style={styles.title}>Discover songs by their traits</Text>
          <Text style={styles.subTitle}>please sign in with your Spotify account</Text>
        </View>
        <SvgBackground/>
      </View>
      <View style={styles.loginScreenButton}>
        <LoginButton/>
      </View>
      {/* <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" /> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center'
  },
  title:{
    ...Platforn.select({
    ios:{
        fontSize: 30,
        fontWeight: 'bold',
    }
  })},
  subTitle: {
    fontSize: 18,
    fontWeight: '200',
  },
  backgroundWrapper: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    justifyContent: 'center',
    alignItems: 'center'
  },
  loginScreenButton:{
    ...Platform.select({
      ios:{
        marginRight:40,
        marginLeft:40,
        marginTop:10,
        paddingTop:8,
        width: '60%',
        paddingBottom:8,
        backgroundColor:'#e21051',
        borderRadius:10,
        borderWidth: 1,
      },
      default: {
        backgroundColor: 'blue',
      }
    })
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  wrapper: {
    textAlign: 'center',
    width: '50%',
    marginBottom: 15
  }
});

export default Login
